meancomp <- function(nsim,nrv,param) {    vector <- replicate(nsim, rexp(nrv, param))    exp_mean <- apply(vector,2, mean)    list(mean(exp_mean),var(exp_mean),exp_mean)}drawhist<-function(a,xn,yn){    h<-hist(a, xlab='xs', col='yellow',main='Histogram')    lines(xn, yn, col="blue", lwd=2)    h}library(shiny)shinyServer(function(input, output) {z<-reactive({meancomp(input$nsim,input$nrv,input$param)})l<-reactive({z()[[3]]})output$l<-renderPrint({l()})min_sim<-reactive({min(l())})max_sim<-reactive({max(l())})mean_teor<-reactive({1/input$param})sd_teor<-reactive({sqrt((1/input$param)^2/input$nrv)})xnorm<-reactive({seq(min_sim(),max_sim(),length=200)})ynorm<-reactive({dnorm(xnorm(),mean=mean_teor(),sd=sd_teor())})output$mean <- renderText({z()[[1]]})output$var <- renderText({z()[[2]]})output$mean_teor <- renderText({mean_teor()})output$var_teor <- renderText({(1/input$param)^2/input$nrv})output$newHist <- renderPlot({    hist(l(),col="yellow", main="Histogram of the simulated data",xlab="Simulated mean",ylab="Frequency",prob=TRUE)    if (input$flag==1){        lines(xnorm(),ynorm(),col="red",lwd="4")}})})